#define _Smooth(p,r,s) smoothstep(-s, s, p-(r))
#define PI 3.141592
#define TPI 6.2831

#define RADIUS .15
#define STRETCH .3

float df(vec2 position)
{
    return min(length(position - vec2(0.,STRETCH)),max(position.x , step(STRETCH,position.y)) )+ step(position.y,.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 position =  (fragCoord -.5*iResolution.xy) / iResolution.y ;

    position.x = abs(position.x);


    float f = df(position);


    float sa = .86602; // sa = sin(PI * 2./3.)
    position = mat2(-.5,sa,-sa,-.5) * position;

    position.x = abs(position.x);
    f = min(f,df(position));


    f = _Smooth(RADIUS,f,.005);



    vec3 col = vec3(f);
	fragColor = vec4(col,1.0);
}
