void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy; 



    float sin_factor = sin(iTime*0.00001);
    float cos_factor = cos(iTime*0.00001);
    uv = vec2((uv.x - 0.5), uv.y - 0.5 ) * mat2(cos_factor, sin_factor, -sin_factor, cos_factor);

    uv += 0.5;


    if(iFrame < 10 || iMouse.z >0.0) {
    	fragColor = texture(iChannel1, uv);
    } else {
        uv = -1.0 + 2.0 * uv;
        uv *= 0.995;
        uv.y += 0.001;
        vec3 r = texture(iChannel0, uv*0.5 + 0.5).rgb;

        r.r += r.g*0.001;
        r.g += r.b*0.001;
        r.b += r.r*0.001;
        r = mod(r, vec3(1.0));

    	fragColor = vec4(vec3(r), 1.0);
    }
}
