precision highp float;

#define sin1(x) (.5+sin(x)*.5)
#define cos1(x) (.5+cos(x)*.5)
#define ss smoothstep
const float pi_2 = asin(1.);
const float pi   = pi_2 * 2.;
const float pi2  = pi_2 * 4.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy/iResolution.xy;
	vec3 col=vec3(.0);
    float scale = iResolution.y/iResolution.x;
	vec2 p=-1.+2.*uv;
	p.x*=1./scale;
	p*=1.4;

	float d=length(p);
	p.y*=.6;
	float e=length(p
		+.5*ss(.0,-.6,p.y)
		*vec2(cos(iTime*1.5+p.y*scale*8000.),.0));
    col+=1.;
	col*=ss(.8,.79,mix(e,d,step(.0,p.y)));
	col+=.4;
	col*=vec3(1.,.5,.0)/(d*mix(e,d,step(.0,p.y)));
	col*=sqrt(.6);
	col*=mix(1.,.7,step(.0,p.y));
	fragColor = vec4( col, 1.0 );
}
