void mainImage( out vec4 O,  vec2 U )
{
	vec2 R = iResolution.xy, 
		 M = iMouse.xy/R;
    U /= R;

    float a = M.x/2.-.25;
    vec3 m = abs(U.x*2.-vec3(3,2,4)/3.);
    m.gb = 1.-m.gb;

    m += a;

    if (M.y > .5) m = smoothstep(0., 1., m);

    O = sqrt(vec4(m, 1));
}

