const float p = acos(-1.);
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 R = iResolution.xy,
         q = ( 2.*fragCoord-R ) / R.y;
 	vec3 c = vec3(0);
    float b = length(q)/2.;
    c*=1.-b;
    float t= sin(iTime*p/3.)*2.*p;
    vec2 a = vec2(cos(t),sin(t))/2.;
    vec2 d = vec2(cos(t+p),sin(t+p))/2.;
    float z = .2;
    float k = .01;
    float aspo = abs(length(q-d)-z);
    if (aspo<k) {
    	c=1.-vec3(aspo/k);    
    }
    float l = length(q-a);
    if (l<z-k/2.) {
        c=vec3(pow(1.-(l/(z-k/2.)),1./4.));
    }

    vec2 dca = normalize(a-q);
    vec2 dcb = normalize(d-q);
    vec3 k1 = vec3(248,251,234)/256.;
    vec3 k2 = vec3(204,210,244)/256.;
   	vec3 add_a = mix(k2,k1,dot(dca,vec2(1,0)*.5+.5))/abs(l-(z-k/2.));
   	vec3 add_b = mix(k2,k1,dot(dcb,vec2(1,0)*.5+.5))/aspo;
    add_a/=50.;
    add_b/=50.;
   	c+=(add_a+add_b);
	fragColor = vec4(c,1.0);
}
