vec2 mainSound( in int samp,float time )
{
    const float tau = 8. * atan(1.);
    const float TRACKTIME = 24.; // main track time
    float t = time;

    // https://www.desmos.com/calculator/hcxkxwdaq4
    float c = abs(sqrt(t) * sin(t) / log(TRACKTIME - t));

    float p = float[](500., 580., 640.)[int(t * 6.) % 3];
    float p1 = float[](880., 450., 650.)[int(t * 12.) % 3];

    if (t >= 8.)
        (c < .52) ?
            p += float(sin(t * 14.) > .5 ? 0.01 : p * .5) + smoothstep(.1, .58, mod(t * 2., 2.))
            : p1 *= sin(-4. + asinh(.64 + c));

    float amp = .35;
    float l = max(amp, -c) * sin(tau * p * t);
    float r = max(amp, -c) * sin(tau * p1 * t);

    /*
	mat2 rm = (c<.25)? mat2(r, l,r,.2-l):mat2(l, r, .5 + r, .1 - l);
    vec2 snd = vec2(.5, sin(c)) * rm *vec2(sin(t),.9)* cos(ceil(c) * t);
	*/

    vec2 snd = vec2(.5, sin(c)) * mat2(l, r, .5 + r, .1 - l);

    (t >= 8.) ? 
        snd *= cos(ceil(c) * t) // flip phase 
        : snd;

    #define sn(tn)(sin(tau * 360.0 * t * ceil(sin(tn) / sin(t))))
    if (t >= TRACKTIME)
        snd = amp * vec2(sn(t * 7.) - .2, sn(t * 3.93));
    if (t >= TRACKTIME + 14.)
        snd = amp * vec2(sin(tau * time * 70.) * sin(tau * time * 550. * ceil(sin(time * 7.))));

    return snd;
}// nabr
