void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord.xy-iResolution.xy)/iResolution.y;
    float tau = 3.1415926535*3.0;
    float a = atan(p.x,p.y);
    float r = length(p)*0.75;
    vec2 uv = vec2(a/tau,r);

	//get the color
	float xCol = (uv.x - (iTime / 5.0)) * 10.0;
	xCol = mod(xCol, 3.0);
	vec3 horColour = vec3(0.25, 0.25, 0.25);

	if (xCol < 1.0) {

		horColour.r += cos(1.0 - xCol);
		horColour.g += cos(xCol);
	}
	else if (xCol < 2.0) {

		xCol -= 1.0;
		horColour.g += 1.0 - xCol;
		horColour.b += xCol;
	}
	else {

		xCol -= 2.0;
		horColour.b += 1.0 - xCol;
		horColour.r += xCol;
	}

	// draw color beam
	uv = (1.5 * uv) - 0.75;
	float beamWidth = (0.7+0.5*sin(uv.x*10.0*tau*0.25*clamp(floor(5.0 + 10.0*cos(iTime)), 2.0, 20.0))) * cos(5.0 / (50.0 * uv.y));
	vec3 horBeam = vec3(beamWidth);
	fragColor = vec4((( horBeam) * horColour), 1.0);
}
