void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec4 n1 = texture( iChannel0, uv*1.6 + iTime*0.01 );
    vec4 n2 = texture( iChannel0, uv*1.6 + iTime*0.02 );


    float shp = n1.x ;
    float shp1 = smoothstep( 0.1, 0.11, shp );// - smoothstep( 0.2, 0.31, shp );

    float shp2 = smoothstep( 0.1, 0.11, shp ) - smoothstep( 0.1, 0.2, shp );;


    vec4 v1 = texture( iChannel1, uv + shp1*0.0 );
    vec4 v2 = texture( iChannel1, uv - shp2*0.03 );

    v1 = smoothstep( 0.2, 0.92, v1 );
    v2 = smoothstep( 0.2, 0.92, v2 );

    // Output to screen
    fragColor = vec4( mix(v1.rgb,v2.rgb,0.9) - shp2*0.2, 1.0 );

}
