void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	vec2 uvMeio = (uv - vec2(0.5,0.5));
    vec2 uvFinal = vec2(uvMeio.x*3.0, uvMeio.y*1.8);

    float mag = length(uvFinal);
    fragColor = vec4(1,round(mag),round(mag),1);



 }
