#define PI 3.141592654

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.y *= 4.; uv.y -= 2.;

    float f = texture(iChannel0, vec2( uv.x, 0.75)).x;
    float a = texture(iChannel0, vec2( uv.x, 0.25)).x;

    float c = 1. - clamp(0., 1., pow(abs(uv.y - sin(2. * PI * uv.x / f) * a), 0.5));

    fragColor = vec4(vec3(0.15 * c, 0.15 * c, c), 1.0);
}
