void mainImage(out vec4 O, vec2 p) {
    float   C = iTime, a=sin(C), b=cos(C);                                 
    mat3    U = mat3(a,b*b,b*a,0,a,-b,-b,a*b,a*a);                         
    vec3    B = vec3(0,0,7)*U, d=normalize(.5-vec3(p/iResolution.y,1.8))*U;
    for(int E =0;++E<9;) B+=d*(a=length(max(abs(B)-1.+b*b,0.))-1.3*b*b);    
    O.rgb = a<1.?cos(B):d*d;
}
