// what I am trying to achieve is actually http://www.lifo.gr/icache/520/377/1/793964_Screenshot_23_34.jpg
// From the start of here https://www.youtube.com/watch?v=-4SSYqGxk1w

#define PI 3.14159265359

float rand(vec2 co)
{
	return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453) - 0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float time = iTime;
    vec2 resolution = iResolution.xy;

	float aspect = resolution.x / resolution.y;
	vec2 pos = fragCoord.xy / resolution.x - vec2(0.5, 0.5 / aspect);

	float t = mod(time, 2.0);
	if (t > 1.0) t = 1.0;
	//t = 1.0;

	vec2 m = vec2(0.005 - pow(t, 3.0) * 0.04, sin(t) * 0.004);

	vec2 p1 = pos - m;
	vec2 p2 = pos + m;


	float r = length(pos);
	float a = atan(pos.y, pos.x);

	float wave = pow(sin(pow(length(p1) / length(p2), 0.5) * 64.0), 2.0);


	float c = pow(sin(a * 128.0) * 0.5 + 0.5, 2.0) * pow(r, 2.0) * wave;

	float logo = 0.0;
	for (int i = 0; i<2; i++)
	{
		float ii = float(i) - 0.5;
		if (sqrt(pow((pos.x + ii * 0.04) * 2.0, 4.0) + pow(pos.y * 1.8, 6.0)) < 0.002) logo = 1.0;
		if (sqrt(pow((pos.x + ii * 0.04) * 6.0, 4.0) + pow(pos.y * 7.0, 6.0)) < 0.002) logo = 0.0;
		if (length(pos.x + ii * 0.07) < 0.01 && length(pos.y) < 0.007) logo = 0.0;
	}
	if (length(pos.x - 0.022) < 0.02 && length(pos.y - 0.003) < 0.006) logo = 1.0;
	if (length(pos.x + 0.022) < 0.02 && length(pos.y - 0.007*sin(pos.x * 90.0 + 3.0) + 0.005) < 0.006) logo = 1.0;

	if (logo < 0.0) logo = 0.0;
	if (logo > 1.0) logo = 1.0;

	logo *= 0.5;

	c += rand(pos*time) * 0.025;

	vec4 color = vec4(4.0 * c, 4.0 * c, 0.1 + 4.0 * c, 1.0) + vec4(logo, logo, logo, 1.0);

	fragColor = color;
}
