float s( float b ){
    return abs( b / 2. - mod( iTime * 4e2, b ) );
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec4 p = fragCoord.xyxy + 800., q = 2. * p;
	fragColor.rgb = normalize( vec3( s( p.x + p.y ), s( q.x - p.y ), s( q.y - p.x ) ) );
}
