// Based on: https://gist.github.com/patriciogonzalezvivo/114c1653de9e3da6e1e3
vec3 hsv2rgb(float c) {
	vec3 p = abs( fract(c + vec3(3,2,1)/3.) * 6. - 3.) -1.;
	return clamp(p, 0., 1.);
}


// Simon Gladman | November 2017
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy / iResolution.xy) - 0.5;

 	float angle = sin(atan(uv.x, uv.y) * 8.0 + iTime); 

    angle *= sin(length(uv) * 24.0);

    float value = abs(angle + (iTime * 0.4));

    vec3 rgb = hsv2rgb(value); 

    fragColor = vec4(rgb,1.0);
}
