float circle(vec2 uv, vec2 position, float radius, float blur)
{
    float distance = length(uv - position);
    float c = smoothstep(radius, radius - blur, distance);

    return c;
}

float smiley(vec2 uv, vec2 position, float size)
{
    uv -= position;
    uv *= size;

    float mask = circle(uv, vec2(.0), .4, .01);
    mask -= circle(uv, vec2(-.14, .2), .08, .01);
    mask -= circle(uv, vec2(.14, .15), .08, .01);

    float mouth = circle(uv, vec2(0., 0.), .3, .02);
    mouth -= circle(uv, vec2(0., .1), .3, .02);

    mask -= mouth;

    return mask;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= .5;
    uv.x *= (iResolution.x / iResolution.y);

    vec3 col = vec3(0.);

    float mask = smiley(uv, vec2(0., .0), 1.);

    col = vec3(1., 1., 0.) * mask;

    fragColor = vec4(vec3(col), 1.);
}
