void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv += vec2(cos(iTime)/2.0, sin(iTime)/1.5);
    vec4 color = texture(iChannel0, uv);
    int n = int(abs(sin(iTime * 0.8)) * 15.0 + 1.0);
    for (int i = 2; i < 40; ++i) {
        if (i < n) {
	        float xoff = 0.1 * float(i);
    	    float yoff = 0.08 * float(i) * sin(iTime) * cos(iTime);
       	 	vec2 uv2 = uv + vec2(xoff, yoff);
       		vec4 c = texture(iChannel0, uv2);
       		color = sqrt(mod(color * c * 2.0, 1.0) * 1.5);
            color *= color;
        }
    }
    fragColor = color;
}
