// by Nikos Papadopoulos, 4rknova / 2016
// WTFPL

float message(vec2 p) {
    int x = int(p.x+1.)-1, y=int(p.y)-10,  i;
    if (x<1||x>32||y<0||y>2) return -1.; 
    i = ( y==2? i=  757737252: y==1? i= 1869043565: y==0? 623593060: 0 )/ int(exp2(float(32-x)));
 	return i==2*(i/2) ? 1. : 0.;
}

void mainImage(out vec4 c, vec2 p)
{
    if   (iResolution.y<200.){float t=message(2.*p.xy/8.); if(t>=0.){ c=vec4(t);return; }}
    else 					 {c = texture(iChannel0, p.xy / iResolution.xy);        }
}

// by Nikos Papadopoulos, 4rknova / 2015
// Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

vec2 r(float t)
{
    vec4 v = vec4(.5,.9,.1,fract(cos(iDate.w+t)*43758.5453)*3.);
    return v.w < 1. ? v.xy : (v.w < 2. ?  v.zz : v.yz);
}

void mainImage(out vec4 c, in vec2 p)
{
    vec2 v, e = r(0.);
    for (float i = 0.; i < 5e2; ++i) {
        e = mix(r(i), e, .5);
        v = e - p / iResolution.xy;
        if (dot(v,v) < 1e-6) {
            c = vec4(1); 
            return;
        }

    }	
    discard;
}
