vec2 aspect;
vec2 uv;
vec2 pan;

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(32.9898,78.233))) * 43758.5453);
}

float rand1(float i) {
    return rand(vec2(i, i));
}

vec4 star(int n) {
    float fn = float(n);
    vec2 p = vec2(
        rand1(0.12+float(n))* 3.,
        rand1(0.5+float(n)) * 3.);
   	float pf = rand1(fn*0.63)*0.5+0.5;
    p += pan*pf;
    p = mod(p, 3.);
    p -= vec2(0.5);

    vec3 rgb = vec3(
        rand1(0.654*fn),
        rand1(0.953*fn),
        rand1(0.123*fn));

    float blink = rand1(0.868*fn)+0.8;

    float dist = length(uv-p);

    float i;    
    i = 0.002 * pow(length(uv-p), -1.2) * pf;

    if (rand1(0.589*fn*iTime) > blink) {
        i *= 0.5;
    }

    return vec4(rgb*i, 1);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    aspect = iResolution.xy / min(iResolution.x, iResolution.y);
	uv = fragCoord.xy / min(iResolution.x, iResolution.y);

	fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
    fragColor = vec4(0);

    pan = vec2(sin(iTime)+cos(iTime*0.21),
               sin(iTime*0.52)+cos(iTime*0.36));

    for (int i=0; i<200; ++i) {
        fragColor += star(i);
    }


}
