void mainImage(out vec4 b, in vec2 f) {

    vec2 c;

    float 
        t = iTime*1.1,
        l = 0., s, a;

    f = f.xy / iResolution.xy;

    for (float k = 0.; k < 1024.; k++) {
        float jore = 10.*t*abs(cos(t*0.1));
        if (k > jore) break;
        s = pow(fract(k / jore + t / 4.), 5.),
		a = (k / 1.2 + t);
        c = vec2(tan(a), cos(a)) * (s * s + s*(k*0.00004)) + .5; 

        if (distance(c, f) < s*cos(k)*tan(t*0.1)*1.) l = max(s+sin(k)*cos(k+t), l-cos(jore*s*0.01+t)*10.);

    }

    b = l * vec4(.63*3.,.45-0.1-cos(t+gl_FragCoord.y)*0.01,.45*3., .5);

}
