vec2 v0 = vec2(0.33, 0.33);
vec2 v1 = vec2(0.66, 0.33);
vec2 v2 = vec2(0.50, 0.66);

float wedge(vec2 a, vec2 b)
{
    return a.x*b.y - a.y*b.x;
}

float triangle(vec2 x, float fuzz)
{
    vec2 e0 = v1 - v0;
    vec2 e1 = v2 - v1;
    vec2 e2 = v0 - v2;

    float b0 = wedge(e0, x - v0);

    if(b0 <= -0.00001) { return 0.0; }

    float b1 = wedge(e1, x - v1);

    if(b1 <= -0.00001) { return 0.0; }

    float b2 = wedge(e2, x - v2);

    if(b2 <= -0.00001) { return 0.0; }

    float N = wedge(e1, -e0);

    if(fuzz == 0.0) return 1.0;

    return min(1.0, (pow(b0*b1*b2, 0.33) * 3.0 / N) / fuzz);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float a = triangle(uv, 0.0);


    fragColor = vec4(1.0, 1.0 - a, 1.0 - a, 1.0);
}
