void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 lowerLeftDir = normalize(vec2(0.2, 1));
    vec2 upperLeftDir = normalize(vec2(0.4, 0.5));
    vec2 upperRightDir = normalize(vec2(0.85, 0.1));
    vec2 lowerRightDir = normalize(vec2(0.5, 0.4));

    float speed = iTime * 0.3;

    vec4 tex1 = texture(iChannel0, uv + (lowerLeftDir * speed));
    vec4 tex2 = texture(iChannel0, uv + (upperLeftDir * speed));
    vec4 tex3 = texture(iChannel0, uv + (upperRightDir * speed));
    vec4 tex4 = texture(iChannel0, uv + (lowerRightDir * speed));

    // Need distance to pixel
    float d1 = 1.0 - distance(uv, vec2(0, 0));
    float d2 = 1.0 - distance(uv, vec2(0, 1));
    float d3 = 1.0 - distance(uv, vec2(1, 1));
    float d4 = 1.0 - distance(uv, vec2(1, 0));

    tex1 *= d1;
    tex2 *= d2;
    tex3 *= d3;
    tex4 *= d4;

    vec4 combined = (tex1 + tex2 + tex3 + tex4) / (d1 + d2 + d3 + d4);

    fragColor = vec4(combined.rgb, 1);


//	fragColor = vec4(tex1.rgb, 1.0);
}
