void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 a = vec2(0.0, 1.0);
    vec2 b = vec2(0.5, 0.0);
    vec2 c = vec2(1.0, 1.0);

    vec2 cb = normalize(c - b);
    vec2 uvb = normalize(uv - b);
    vec2 up = vec2(0.0, 1.0);
    float angle = dot(up, cb);

	if(dot(uvb, up) < angle)
    {
		fragColor = vec4(0.0);
        return;
    }

    vec3 pink = vec3(255.0, 163.0, 196.0) / 255.0;
    vec3 blue = vec3(163.0, 235.0, 255.0) / 255.0;

    vec3 col = mix(pink, blue, uv.x);
    col = mix(col, vec3(1.0), 1.0 - uv.y);

    col = pow(col, vec3(4.0));

	fragColor = vec4(col.rgb, 1.0);
}
