vec2 Location = vec2(0.5,0.3);
vec2 Location2 = vec2(0.6,0.3);
vec2 Location3 = vec2(0.4,0.3);
float Radius = 0.1;
vec3 BgColor = vec3(0.0,0.0,0.0);
vec3 Color2 = vec3(0.0,1.0,0.0);
vec3 Color3 = vec3 (1.0,0.0,1.0);
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec3 Color1 = vec3(0.0 + 0.5 * sin(iTime),1.0 + 0.5 * cos(iTime),1.0 + 0.5 * sin(iTime));

vec2 uv = fragCoord.xy / iResolution.xy;
uv.y *= iResolution.y / iResolution.x;

//Question 4
vec4 Layer1 = vec4(BgColor,1.0);
vec4 Layer2;
vec4 Layer3;
vec4 Layer4;

Location.y += (cos(iTime * 4.0) * 0.1);
Location.x += (sin(iTime * 4.0) * 0.1);
//Location3.y *= (sin(iTime * 3.0)) + 1.0;


Layer2 = vec4(Color1,1.0 - step(Radius, length(Location - uv)));

Layer3 = vec4(Color2, 1.0 - step(Radius, length(Location2 - uv)));

Layer4 = vec4(Color3, 1.0 - step(Radius, length(Location3 - uv)));

vec4 Composite = mix(Layer1, Layer4, Layer4.a);
Composite = mix(Composite, Layer3, Layer3.a);
Composite = mix(Composite, Layer2, Layer2.a);

fragColor = Composite;

}
