//declare color variables
vec3 BgColor = vec3(1.0,1.0,1.0);
vec3 Color1 = vec3(1.0,0.0,0.0);
vec3 Color2 = vec3(0.0,1.0,0.0);
vec3 Color3 = vec3(0.0,0.0,1.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    //declare layers
    vec4 Layer1 = vec4(BgColor, 1.0);
    vec4 Layer2, Layer3, Layer4, Layer5;

    //declare layer alphas
    float Rec1, Rec2, Rec3, Rec4;

    //Red rectangle (house)
    float Rec1x = step(.3, uv.x) * step(uv.x, .7);
    float Rec1y = step(.1, uv.y) * step(uv.y, .6);
    Rec1 = Rec1x * Rec1y;

    //Green rectangle (chimney)
    float Rec2x = step(.55, uv.x) * step(uv.x, .65);
    float Rec2y = step(.6, uv.y) * step(uv.y, .8);
    Rec2 = Rec2x * Rec2y;

    //Left window
    float Rec3x = step(.35, uv.x) * step(uv.x, .45);
    float Rec3y = step(.3, uv.y) * step(uv.y, .45);
    Rec3 = Rec3x * Rec3y;

    //Right window
    float Rec4x = step(.55, uv.x) * step(uv.x, .65);
    float Rec4y = step(.3, uv.y) * step(uv.y, .45);
    Rec4 = Rec4x * Rec4y;

    //Assign layers
    Layer2 = vec4(Color1, Rec1);
    Layer3 = vec4(Color2, Rec2);
    Layer4 = vec4(Color3, Rec3);
    Layer5 = vec4(Color3, Rec4);

    //mix layers
    vec4 Composite = mix(Layer1, Layer2, Layer2.a);
    Composite = mix(Composite, Layer3, Layer3.a);
    Composite = mix(Composite, Layer4, Layer4.a);
    Composite = mix(Composite, Layer5, Layer5.a);
    fragColor = Composite;
}
