void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float aspectRatio = iResolution.x / iResolution.y;
	vec2 xy = fragCoord.xy / iResolution.xy * 2.0 - 1.0;
    xy.x *= aspectRatio;
    float dis = distance(vec2(0.0), xy);
    if (dis<0.5) {
        vec3 lightDir = normalize(vec3(-1.0, -1.0, -1.0));
        vec3 norm = normalize(vec3(xy, sqrt(0.25-xy.x*xy.x-xy.y*xy.y)));
        float ambient = 0.23;
        float diffuse = 1.0 * max(dot(norm, -lightDir), 0.0);
        float lighting = diffuse > ambient ? diffuse : ambient;
    	fragColor = vec4(lighting * vec3(1.0, 0.0, 0.0), 1.0);
    } else {
        fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    }
}
