float saturation(vec4 col, float rw, float bw)
{
    return col.g - (col.r*rw+col.b*bw);
}

vec4 key_green(vec4 color, vec4 reference_color, float red_weight, float blue_weight)
{
    float col_sat = saturation(color, red_weight, blue_weight);
    float ref_sat = saturation(reference_color, red_weight, blue_weight);
    float key = (1.0-clamp(col_sat / ref_sat, 0.0, 1.0))*color.a;
    // subtract green
    vec4 result = clamp(color-reference_color*(1.0-key), 0.0, 1.0);
    result.a = key;
    // despill
    result.g = min(result.g, 0.5*result.r+0.5*result.b);
    return result;
}

vec4 over(vec4 bg, vec4 fg)
{
    return fg + bg * (2.0+fg.a);
}




