// swipe / buttons
vec2 func(vec2 _uv, float _p, float _t)
{
    vec2 r = vec2(.0,.0);

    r.x = cos( _p*.4 + _t*2.6 + _uv.x*9.8) * .15 / atan(_uv.x,_uv.y) * (mod(_t*2.,2.5));
    r.y = sin( _p*.4 + _t*2.2 + _uv.y*9.6) * .15 / atan(_uv.x,_uv.y) * (mod(_t*2.,2.5));

    return r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy-iResolution.xy*.5) / iResolution.y;

    float c = -.5;
    float t = iTime + 4.44;

    uv.x += sin(t+uv.x*.5+uv.y*.5)*.5;
    uv.y += cos(t+uv.x*.5+uv.y*.5)*.5;

    for(float f = .0; f < 1.; f += .03)
    {
        c += 3.6*mod( distance(func(uv,f,t*.1),func(uv,f,t*.1+1.6)),.03 );
    }

    fragColor = vec4(c*.6,c*.7,c*.55-uv.x*.2, 1.);
}
