float f(vec2 p, float t)
{
	p = fract(p/70.+vec2(cos(t), sin(t)))-0.500001;
	return  min(abs(p.x), abs(p.y)) + 0.49999;
}

void mainImage( out vec4 x, in vec2 p )
{
    p /= 3.; 

    vec4 o;
    o -= o;

    mat2 m = mat2(10., -5.4, 5.4, 10.) * 0.09;

	float t = iTime * 0.0051;

	const float iter = 3.;
	float lay = 0., olay;
	for (float i = 0.; i < iter; i++)
	{
		lay = f(p, t * 10. * i / iter);
		if (i > 0.)	lay = min(olay, lay); olay = lay;
		p *= m;
	}
    o += lay;

	o /= 0.53;
    vec4 back = vec4(p.xy/iResolution.xy,0.5+0.5*sin(iTime),255.0);
    vec4 cMixsub = mix(o, back, 2.0);
    vec4 glasscol = vec4(1.0,1.0,1.0,0.5);
    vec4 cMix = mix(refract(glasscol,cMixsub,0.0),reflect(glasscol,cMixsub),0.0);
    x = cMix;
}
