// Gigatron ... conversion from and old Amiga Insane Cracktro .. 

  #define PI 3.1415926

  float offset=8.0; 
  float x3,dx,tmp=0.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv =  fragCoord/iResolution.xy;
    vec4 tx=texture(iChannel0,uv);

    vec2 xsin=uv*480.;

    float tt=iTime*10.;

      offset-=0.006-tt*5.;

      tmp=(tt+xsin.y+offset/2.0)*PI/180.;
      dx=60.*( sin(tmp)+cos(0.0003125*tmp));
	  x3=dx*sin(offset/25.0+xsin.y*PI/360.);


   //  fragColor =vec4(tx.r*xsin.x-x3-100.,tx.g*xsin.x-x3-100. ,tx.b*xsin.x-x3-100. ,1.0)/30.;

   // Sure we can reduce this line,( Master Fabrice comment)  by :

   //  fragColor =vec4(tx*xsin.x-x3-100.) / 30.;

   //however continue to add nice Fx with ignoring green component color under .39 value 

    (tx.g<0.39) ? fragColor =vec4(tx*xsin.x-x3-100.) / 30. : fragColor=vec4(.5);


}
