// entirely based off: https://www.shadertoy.com/view/XtsSW8
vec3 colors = vec3(0.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime * 0.1;
    vec2 uv = fragCoord.xy / iResolution.xy;

    float grid = 2.0 + 25.0 * (sin(time) + 1.0) * 0.5;
    float ratio = 1.0/grid;

    vec2 uv2 = vec2(uv.x - ratio, uv.y);

    if(uv.x < ratio) { 
      uv2 = vec2(uv.x + (grid - 1.0)/grid, uv.y - ratio);
    }

    if(uv.x < ratio && uv.y < ratio){
      uv = fract(uv * grid);
      colors = texture(iChannel0, uv).rgb;
    }else {
      colors = texture(iChannel1, uv2).rgb;
    }

    fragColor = vec4(colors,1.0);
}
