void sample(int d, in vec2 uv, inout vec4 fragColor, int totalPasses)
{

    vec2 step1 = (vec2(d) +0.5) / iResolution.xy;

    fragColor += texture(iChannel0, uv + step1) / float(totalPasses*4);
    fragColor += texture(iChannel0, uv - step1) / float(totalPasses*4);
  	vec2 step2 = step1;
    step2.x = -step2.x;
    fragColor += texture(iChannel0, uv + step2) / float(totalPasses*4);
    fragColor += texture(iChannel0, uv - step2) / float(totalPasses*4);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy/ iResolution.xy ;
    //fragColor = texture(iChannel0, uv);
    sample(0, uv, fragColor, 5);
    sample(1, uv, fragColor, 5);
    sample(2, uv, fragColor, 5);
    sample(2, uv, fragColor, 5);
    sample(3, uv, fragColor, 5);
}
