//---------------------------------------------------------
// AABouncingBalls.glsl
//   v1.0  2016-05-18  one ball
//   v2.0  2016-10-29  two balls
// based on       https://www.shadertoy.com/view/XsVXzm
// tiny version   https://www.shadertoy.com/view/lsVXRw       
//---------------------------------------------------------
const vec3 circle1Color = vec3(0.4, 0.3, 0.5);
const vec3 circle2Color = vec3(0.6, 0.5, 0.3);
vec2 uv = vec2(0);
//---------------------------------------------------------
// draw circle at pos with given radius
//---------------------------------------------------------
float circle(in vec2 pos, in float radius, in float halo)
{
  return clamp (halo * (radius - length(pos)), 0.0, 1.0);
}
//---------------------------------------------------------
// animate circle depending on given time
//---------------------------------------------------------
float animateCircle (float radius, float time, float x, vec3 color)
{
  float xPos = abs(mod(time, 2.0) -1.0) * 1.6;
  vec2 center = vec2(0.085, 0.5) + vec2(xPos, -0.4+0.7*abs(sin(time*5.0)));
  return circle (uv-center, radius, 111.0);
}
//---------------------------------------------------------
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  uv = fragCoord.xy / iResolution.yy;
  float t = iTime / 5.50;

  vec3 color = vec3(1.0);   // background
  color = mix(color, circle1Color, animateCircle(0.1,           t, 111., circle1Color));
  color = mix(color, circle2Color, animateCircle(0.05, 1.+t*0.876, 222., circle2Color));
  fragColor = vec4(color,1.0);
}
