// Mandelbrot Set

vec2 ComplexMul(in vec2 z1, in vec2 z2) {
    return vec2(z1.x * z2.x - z1.y * z2.y, 
                z1.x * z2.y + z1.y * z2.x);
}

vec2 ComplexSqr(in vec2 z) {
    return ComplexMul(z, z);
}

float Mandelbrot(in vec2 c) {
	vec2 z = vec2(0.0f);
    for (int i = 0; i < 200; ++i) {
        z = ComplexSqr(z) + c;

        if (1000.0f < dot(z, z)) {
            return 0.0f;
        }
    }

    return 1.0f;
}

// Bifurcation Diagram

float LogisticMap(float x, float k) {
    float y = x;

    for (int i = 0; i < 500; ++i) {
        y = k * y * (1.0f - y);
    }

    uint match = 0u;

    for (int j = 0; j < 500; ++j) {
        y = k * y * (1.0f - y);
        match |= uint(abs(x - y) < 1e-3f);
    }

    return float(match);
}

const vec2 min_kx = vec2(2.4f, 0.0f);
const vec2 max_kx = vec2(4.0f, 1.0f);

void mainImage(out vec4 color, in vec2 fragCoord) {
    vec2  uv     = fragCoord / iResolution.xy;
    vec2  kx     = mix(min_kx, max_kx, uv);
    float bvalue = LogisticMap(kx.y, kx.x);

    vec2  c      = vec2(0.5f * kx.x * (1.0f - 0.5f * kx.x), kx.y - 0.66f);
    float mvalue = Mandelbrot(c);

    color = vec4(mvalue, 0.0f, bvalue, 1.0f);
}
