float opS( float d1, float d2 )
{
    return max(-d1,d2);
}

float opU( float d1, float d2 )
{
    return min(d2,d1);
}

vec2 rotate(vec2 v, float a){
	float t = atan(v.y,v.x)+a;
    float d = length(v);
    v.x = cos(t)*d;
    v.y = sin(t)*d;
    return v;
}

float roomWidth = .5;

vec2 map( in vec3 pos ){
  	float rep = 4.;
	vec3 p = pos;

    vec2 idx = floor((abs(pos.xz)) / 0.2)*0.5;

    float clock = iTime*4.;
    float phase = (idx.y+idx.x);

    float anim = sin(phase + clock);

    float i = pModPolar(pos.xz, rep);
    pos.x -= .75 + anim*0.1;


    p.xz = rotate(p.xz, iMouse.x*0.01);


    pos.xz = rotate(pos.xz, iMouse.x*0.02);


    float dist = fOctahedron(pos,roomWidth*.6);

    dist = opU(dist, fOctahedron(p, roomWidth*1.25 + anim*0.05));

    vec2 res = vec2(dist, 0.);

    return res;
}

vec2 castRay( in vec3 ro, in vec3 rd )
{
    float tmin = 0.0;
    float tmax = 60.0;

    float t = tmin;
    float m = -1.0;
    for( int i=0; i<60; i++ )
    {
   		vec2 res = map( ro+rd*t );
        if(  t>tmax ) break;
        t += res.x;
    	m = res.y;
    }

    if( t>tmax ) m=-1.0;
    return vec2( t, m );
}


float softshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
	float res = 1.0;
    float t = mint;

    for( int i=0; i<20; i++ )
    {
		float h = map( ro + rd*t ).x;
        res = min( res, 8.0*h/t );
        t += clamp( h, 0., 0.1 );
        if(t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );

}

vec3 calcNormal( in vec3 pos )
{
	vec3 eps = vec3( 0.01, 0.0, 0.0 );
	vec3 nor = vec3(
   map(pos+eps.xyy).x - map(pos-eps.xyy).x,
   map(pos+eps.yxy).x - map(pos-eps.yxy).x,
   map(pos+eps.yyx).x - map(pos-eps.yyx).x );
	return normalize(nor);
}

float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float hr = 0.01 + .15*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos ).x;
        occ += -(dd-hr)*sca;
        sca *= .95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );    
}

vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

vec3 render( in vec3 ro, in vec3 rd )
{
    vec3 col = vec3(1.);
    vec2 res = castRay(ro,rd);
    const vec3 a = vec3(0., 0., 0.);
    const vec3 b = vec3(1., 1.0, 1.0);
    const vec3 c = vec3(1.0, 1.0, 1.0);
    const vec3 d = vec3(.0,.5,.5);

    if (res.x > 6.) return palette(.3,a,b,c,d);

    vec3 pos = ro + res.x*rd;
    vec3 nor = calcNormal( pos );
    vec3 ref = reflect( rd, nor );

   	float occ = calcAO( pos, nor );
    float dom = smoothstep( -0.6, 20.6, ref.y );

    dom *= softshadow( pos, ref, .9, .15 );

    col = palette(3.+res.x*.1,a,b,c,d)*occ*length(ref)*(1.+dom);
    col = mix( col, vec3(.0), max(1.0-exp( -0.05*res.x ),clamp(res.x*0.1,0.,1.)) );

    return vec3( clamp(col,0.0,1.0) );
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{
	vec2 q = fragCoord.xy/iResolution.xy;
    vec2 p = -1.0+2.0*q;
	p.x *= iResolution.x/iResolution.y;

	vec3 ro = vec3(-3., roomWidth*3., 1.5 + cos(iTime*2.)*0.3 );
  	vec3 ta = vec3(0.,.0,.0);
    mat3 ca = setCamera( ro, ta, 0. );
	vec3 rd = ca * normalize(vec3(p.xy,2.));
    vec3 col = render( ro, rd );
	col = pow(col, vec3(1.5));
    fragColor = vec4( col, 1.0 );
}

// This buffer is the feedback loop

// iq noise

float hash( float n )
{
    return fract(sin(n)*43758.5453123);
}

float noise( in vec2 x )
{
    vec2 p = floor(x);
    vec2 f = fract(x);

    f = f*f*(3.0-2.0*f);

    float n = p.x + p.y*157.0;

    return mix(mix( hash(n+  0.0), hash(n+  1.0),f.x),
               mix( hash(n+157.0), hash(n+158.0),f.x),f.y);
}

// hue by netgrind(?)

vec3 hue(vec3 color, float shift) {

    const vec3  kRGBToYPrime = vec3 (0.299, 0.587, 0.114);
    const vec3  kRGBToI     = vec3 (0.596, -0.275, -0.321);
    const vec3  kRGBToQ     = vec3 (0.212, -0.523, 0.311);

    const vec3  kYIQToR   = vec3 (1.0, 0.956, 0.621);
    const vec3  kYIQToG   = vec3 (1.0, -0.272, -0.647);
    const vec3  kYIQToB   = vec3 (1.0, -1.107, 1.704);

    // Convert to YIQ
    float   YPrime  = dot (color, kRGBToYPrime);
    float   I      = dot (color, kRGBToI);
    float   Q      = dot (color, kRGBToQ);

    // Calculate the hue and chroma
    float   hue     = atan (Q, I);
    float   chroma  = sqrt (I * I + Q * Q);

    // Make the user's adjustments
    hue += shift;

    // Convert back to YIQ
    Q = chroma * sin (hue);
    I = chroma * cos (hue);

    // Convert back to RGB
    vec3    yIQ   = vec3 (YPrime, I, Q);
    color.r = dot (yIQ, kYIQToR);
    color.g = dot (yIQ, kYIQToG);
    color.b = dot (yIQ, kYIQToB);

    return color;
}

float fractalNoise(vec2 pos) {
	float n = 0.;
	float scale = 1. / 1.5;
	for (int i = 0; i < 5; i += 1) {
		n += noise(pos) * scale;
		scale *= 0.5;
		pos *= 2.;
	}
	return n;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   	vec2 uv = fragCoord.xy / iResolution.xy;

    // Convert the uv's to polar coordinates to scale up  
    vec2 polarUv = (uv * 2.0 - 1.0);
    float angle = atan(polarUv.y, polarUv.x);

    // Scale up the length of the vector by a noise function feeded by the angle and length of the vector
    float ll = length(polarUv)*0.5 - fractalNoise(vec2(sin(angle*4. + iTime*2.) + length(uv)*10., length(uv)*20. + sin(angle*4.)))*0.005 ;

    vec3 base = texture(iChannel0, uv).rgb;

    // Convert the scaled coordinates back to cartesian
    vec2 offs = vec2(cos(angle)*ll + 0.5, sin(angle)*ll + 0.5);

    // sample the last texture with uv's slightly scaled up
    vec3 overlay = texture(iChannel1, offs.xy).rgb;

    // Since the colors of the iChannel0 are monochrome, set a color channel to zero to do a hue shift
    base.rb = vec2(0., 0.);

    // Apply a hue shift to the overlaid image so it cascades in the feedback loop
    overlay = hue(overlay, .3);

    // Additively blend the colors together
    vec4 col = vec4(clamp(vec3(0.),vec3(1.),base + overlay*0.86), 1.0);

    fragColor = col;
}
