vec2 center = vec2(0.5, 0.65);
float radius = 200.0;
vec3 color = vec3(0.9,0.3,0.5);
float step = 3.0;

vec4 heart(vec2 p, vec2 cen, float rad){
    vec2 relative = p-cen; //relative position 
    float a = atan(relative.x,relative.y)/3.141593;  //angle
    float r = a*rad; //radius
    if(a > 0.75){
        float k = 0.12-abs(a-0.88);
            r -= k;
        }
    if(relative.x > 0.0){
        //right area
        float d = length(p-cen)-r*1.0;
        float t = smoothstep(0.0, step, d);
        return vec4(color,1.0-t);   
    }else{
        //left area
        float d = length(p-cen)-r*-1.0;
        float t = smoothstep(0.0, step, d);
        return vec4(color,1.0-t);
    }
    return vec4(0.5, 0.8, 0.96, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = fragCoord.xy / iResolution.xy;
	//vec4 layer1 = vec4(uv,0.5+0.5*sin(iTime),1.0);
    vec4 layer1 = vec4(0.5, 0.8, 0.96, 1.0);
    vec2 pos = fragCoord;  
    vec4 layer2 = heart(pos, center.xy*iResolution.xy, radius);
    fragColor = mix(layer1,layer2,layer2.a);
}
