const int MAX_MARCHING_STEPS = 255;
const float MIN_DIST = 0.0;
const float MAX_DIST = 100.0;
const float EPSILON = 0.0001;

float ground(in vec3 samplePoint) {
    return samplePoint.y;
}

float scene(vec3 samplePoint) {
    return ground(samplePoint);
}

float shortestDistanceToSurface(inout vec3 samplePos, vec3 eye, vec3 marchingDirection, float start, float end) {
    float depth = start;
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        samplePos = eye + depth * marchingDirection;
        float dist = scene(samplePos);
        if (dist < EPSILON) {
			return depth;
        }
        depth += dist;
        if (depth >= end) {
            return end;
        }
    }
    return end;
}

vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}

vec3 spec(in vec3 samplePos, in vec3 eye, in vec3 lightPos) {
    vec3 N = vec3(0.0, 1.0, 0.0);
    vec3 L = normalize(lightPos - samplePos);
    vec3 V = normalize(eye - samplePos);
    vec3 R = normalize(reflect(-L, N));

    float dotLN = dot(L, N);
    float dotRV = dot(R, V);

    if (dotLN < 0.0) {
        return vec3(0.0, 0.0, 0.0);
    } 

    if (dotRV < 0.0) {
        return vec3(0.4, 0.4, 0.4) * (vec3(1.0, 0.0, 0.0) * dotLN);
    }
    return vec3(0.4, 0.4, 0.4) * (vec3(1.0, 0.0, 0.0) * dotLN + 1.0 * pow(dotRV, 1.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 dir = rayDirection(45.0, iResolution.xy, fragCoord);
    vec3 eye = vec3(0.0, 0.5, 5.0);
    vec3 samplePos = vec3(0.0, 0.0, 0.0);
    float dist = shortestDistanceToSurface(samplePos, eye, dir, MIN_DIST, MAX_DIST);

    if (dist > MAX_DIST - EPSILON) {
        // Didn't hit anything
        fragColor = vec4(1.0, 1.0, 1.0, 1.0);
		return;
    }

    vec3 lightPos = vec3(0.0, 1.0 + sin(iTime) * 0.5, 0.0);

    vec4 specular = vec4(spec(samplePos, eye, lightPos).xyz, 1.0);


    fragColor = vec4(1.0, 0.0, 0.0, 1.0) * specular;
}
