//All my variables

vec2 borderleft = vec2(0.0,0.0);
vec2 borderDimensions = vec2(1.0,1.0);

//house variables
vec2 lowerLeftH = vec2(0.5, 0.0);
vec2 dimensionsH = vec2(0.2,0.45);

//windows variables
vec2 lowBoundLW = vec2(0.4, 0.35);
vec2 lowBoundRW = vec2(0.6, 0.35);
vec2 dimensionsW = vec2(0.05,0.1);

//chimney variables
vec2 lowBoundC = vec2(0.4, 0.8);
vec2 dimensionsC = vec2(0.05,0.1);


//colors
vec3 houseColor = vec3(1.0,0.0,0.0);
vec3 windowColor = vec3(0.0,1.0,0.0);
vec3 chimneyColor = vec3(0.0,0.0,1.0);
vec3 backGroundColor = vec3(1.0,1.0,1.0);

//bools, true means layer is "on" false means layer is "off"
bool houseOn = true;
bool windowsOn = true;
bool chimneyOn = true;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = fragCoord.xy / iResolution.xy;

    //initialize the layers to be later used.
    vec4 sum = vec4(0.0,0.0,0.0,0.0);
    vec4 backGroundLayer = vec4(0.0);
    vec4 layer1 = vec4(0.0);
    vec4 layer2 = vec4(0.0);
    vec4 layer3 = vec4(0.0);

    //apply the aspect ratio to all the positions of each layer component.
    float aspectRatio = iResolution.y / iResolution.x;
    uv.y *= aspectRatio;
	borderleft.y *= aspectRatio;
    lowerLeftH.y *= aspectRatio;
    lowBoundLW.y *= aspectRatio;
    lowBoundRW.y *= aspectRatio;
    lowBoundC.y *= aspectRatio;

    //Draw Background
    float interX = step((borderleft.x), uv.x) - step((borderleft.x)+ borderDimensions.x, uv.x);                             
    float interY = step((borderleft.y), uv.y) - step((borderleft.y) + borderDimensions.y, uv.y);

    backGroundLayer.a = (interX  * interY);//take the product of the interpolation values and set the alpha to it

    backGroundLayer = vec4(backGroundColor,backGroundLayer.a);//now set the layer to the color and the new alpha value

    //Draw red house, same descriptions as the background but with different dimensions and positions
    float interpX = step((lowerLeftH.x - dimensionsH.x), uv.x) - step((lowerLeftH.x) + dimensionsH.x, uv.x);//interpolate the two step functions to get one interpolation value
    float interpY = step((lowerLeftH.y - dimensionsH.y), uv.y) - step((lowerLeftH.y) + dimensionsH.y, uv.y);


    layer1.a = (interpX * interpY) * float(houseOn);//the bools are now in play to determine if the alpha will be 0 or 1.

    layer1 = vec4(houseColor,layer1.a);

    //Draw windows
    float interpXW =  step(lowBoundLW.x - dimensionsW.x, uv.x) - step(lowBoundLW.x + dimensionsW.x, uv.x);
    float interpYW =  step(lowBoundLW.y - dimensionsW.y, uv.y) - step(lowBoundLW.y + dimensionsW.y, uv.y);

    interpXW +=  step(lowBoundRW.x - dimensionsW.x, uv.x) - step(lowBoundRW.x + dimensionsW.x, uv.x);
    interpYW +=  step(lowBoundRW.y - dimensionsW.y, uv.y) - step(lowBoundRW.y + dimensionsW.y, uv.y);

    layer2.a = (interpXW * interpYW) * float(windowsOn);

    layer2 = vec4(windowColor,layer2.a);

    //Draw Chimney
    float interpXC = step((lowBoundC.x - dimensionsC.x), uv.x) - step(lowBoundC.x + dimensionsC.x, uv.x);
    float interpYC = step((lowBoundC.y - dimensionsC.y), uv.y) - step(lowBoundC.y + dimensionsC.y, uv.y);


   	layer3.a = (interpXC * interpYC) * float(chimneyOn);

    layer3 = vec4(chimneyColor,layer3.a);

    //mix the layers together, starting with the chimney, then the house, then the windows.
    sum = mix(backGroundLayer,layer3,layer3.a);
    sum = mix(sum, layer1,layer1.a);
    sum = mix(sum, layer2,layer2.a);

    //Output to screen
    fragColor = sum;
}
