float rand(float x, float y){
    return fract(sin(dot(vec2(x, y) ,vec2(12.9898,78.233))) * 43758.5453) - 0.5;
}

float my_rand(float x, float amp, float idx, float idy) {
    return clamp(x + (rand(idx, idy) * amp), 0.0, 1.0);
}

float randCol(float x, float amp, float idx, float idy) {
    return my_rand(x, amp, idx, idy);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec3 rgb2hsv(vec3 rgb) {
 	float Cmax = max(rgb.r, max(rgb.g, rgb.b));
 	float Cmin = min(rgb.r, min(rgb.g, rgb.b));
 	float delta = Cmax - Cmin;

 	vec3 hsv = vec3(0., 0., Cmax);

 	if (Cmax > Cmin) {
 		hsv.y = delta / Cmax;

 		if (rgb.r == Cmax)
 			hsv.x = (rgb.g - rgb.b) / delta;
 		else {
 			if (rgb.g == Cmax)
 				hsv.x = 2. + (rgb.b - rgb.r) / delta;
 			else
 				hsv.x = 4. + (rgb.r - rgb.g) / delta;
 		}
 		hsv.x = fract(hsv.x / 6.);
 	}
 	return hsv;
 }


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float rows, cols;
    rows = 10.f;
    cols = 10.f;

    float idx = floor(uv.x * rows) / rows;
    float idy = floor(uv.y * cols) / cols;

    vec3 startingColor = vec3(.8, 0.0, 0.5);

    vec3 col = startingColor;

    // Muck w/ rbg
    col.x = randCol(col.x, 0.0, idx, idy);
    col.y = randCol(col.y, 0.0, idx, idy);
    col.z = randCol(col.z, 0.0, idx, idy);

    // Convert to HSV
    vec3 hsv = rgb2hsv(col);

    // Muck w/ hsv
    //hsv.x = randCol(hsv.x, 0.5, idx, idy);
    //hsv.y = randCol(hsv.y, 0.4, idx, idy);
    hsv.z = randCol(hsv.z, 0.0, idx, idy);

    if (rand(idx, idy) > 0.0)
        hsv.x = hsv.x + 0.5;


    col = hsv2rgb(hsv);


