vec2 func(vec2 _uv, float _p, float _t)
{
    vec2 r = vec2(.0,.0);

    r.x = .2/tan( _p*5.4 + _t*2.6 + _uv.x*2.8 + sin(_t*2.+length(_uv)*4.)*3. ) * .026 / atan(_uv.x,_uv.y) * (mod(_t*2.,2.5));
    r.y = .7/tan(_t+length(_uv)*5.)/cos( _p*.4 + _t*2.2 + _uv.y*22.6 + cos(_t*2.+length(_uv)*2.)*12. ) * .025 / (atan(_uv.x,_uv.y)+_t*.1) * (mod(_t*2.,.5));

    return r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy-iResolution.xy*.5) / iResolution.y;

    float c = -.5;
    float t = iTime + 1.4;

    uv.x += sin(t+.1/uv.x*.5/uv.y*.15)*.4;
    uv.y += cos(t+uv.x*4.5+uv.y*.15)*.2;

    for(float f = .0; f < 1.; f += .035)
    {
        c += 2.7*mod( distance(func(uv,f,t*.15),func(uv,f,t*.15+1.)),.04-uv.y*.03 );
        c -=  .8*mod( distance(func(uv,f,t*.15),func(uv,f,t*.15+7.)),.05-uv.x*.06 );
    }

    fragColor = vec4(c*.85,c*.8-length(uv)*.05,c*.6-uv.y*.15, 1.);
}
