void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy / iResolution.xy;
    vec2 q = p - vec2 (0.5* (-0.5 *sin (iTime) + 0.5), 0.7);
    vec4 animFrag = vec4(p,0.3+0.3*sin (iTime),1.0);

    vec4 col = mix(vec4 (1.0,0.4,0.1, 1.0), vec4 (1.0, 0.8, 0.3, 1.0), sqrt(p.y)); 
    col += animFrag;
    float wave = sin(iTime);
    //krona
   	float r = 0.2 + 0.1 * cos(atan(q.y,q.x)*10.0+20.0*q.x + (8.0 * sin(iTime)));

    col *= smoothstep(r, r+0.01, length (q));


    r = 0.015;
    r += 0.002*cos(120.0*q.y) *sin(iTime);
    r += exp(-40.0 * p.y);
    col *= 1.0 - (1.0 - smoothstep(r, r + 0.002, abs (q.x-0.25 *sin(2.0*q.y * wave))))* (1.0 - smoothstep(0.0, 0.1, q.y));
    fragColor = vec4(col);


}
