//Based on https://www.shadertoy.com/view/XtcXz7#

void mainImage( out vec4 o, in vec2 q )
{
	vec2 u = q / iResolution.xy;

    o = texture(iChannel1, u)+texture(iChannel0, u)+iTime*vec4(.25);

    o = abs(mod(o,2.)-1.); 		//ping-pong glow
    o = .5*(1.+sin(6.28318*o)); //sine glow
}

void mainImage( out vec4 o, in vec2 q )
{
    float r = 25.; //radius
    vec4 k = vec4(texture(iChannel1,vec2((float(32)+.5)/256.0,2.5/3.0)).x>.5);

    o = texture(iChannel0, q/iResolution.xy);


    if((iMouse.w>0.)&&(distance(iMouse.xy,q)<r)){
        o = iTime*vec4(.1,.22,.333,1.);

        if (bool(k.x)) o *= 0.;

        o = abs(mod(o,2.)-1.); 		 //ping-pong glow
        o = .5*(1.+sin(6.28318*o));  //sine glow
    }
}
