void mainImage( out vec4 O, vec2 U )
{
    O = .5+.5*sin(texture(iChannel0, U/iResolution.xy).x + vec4(0,2,-2,0));
}

#define T(i,j) texture(iChannel0,(U+vec2(i,j))/iResolution.xy)
#define c(i,j) (T(i,j).x > 0.)
// #define c(i,j) (T(i,j).x > .1) // variant

void mainImage( out vec4 O, vec2 U )
{
    if (iFrame<30) {
        O = texture(iChannel1,U/256.);
        O = vec4(O.x+O.y/256.+O.z/65536. > 1. ); // 24bit random value. max: 1.004
        return; }

    O = T(0,0);
  //O = T(0,0)*.9; // variant

    for (int j=-1; j<2; j++)
        for (int i=-1; i<2; i++)
          //if ( c(i,j) && c(i+i,j+j) ) O++;
            if (    c(i,j)     && !c(-j,i)     && !c(j,-i)
                 && c(i+i,j+j) && !c(-j-j,i+i) && !c(j+j,-i-i) ) O++;

}
