void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // colours
    vec3 colorBackground = vec3(0.8,0.3,0);
    vec3 colorLine = vec3(0.0,0.0,0.0);

    // let us draw something :P
    float radY = 0.5*iResolution.y; //
    float faceWidth = 20.0;

    bool isBackground = true;

    // circle
    vec2 PC = (fragCoord.xy - iResolution.xy*0.5);
    vec2 C = vec2(0.0,0.0);
    isBackground = isBackground &&
        (abs((PC.x-C.x)*(PC.x-C.x)+(PC.y-C.y)*(PC.y-C.y) - radY*radY) > faceWidth*faceWidth);

    // eyes
    vec2 eye1 = vec2(-0.3*radY,0.5*radY);
    vec2 eye2 = vec2(0.3*radY,0.5*radY);
    float eyeD = 10.0;
    isBackground = isBackground &&
        ((PC.x-eye1.x)*(PC.x-eye1.x)+(PC.y-eye1.y)*(PC.y-eye1.y) > eyeD*eyeD);
    isBackground = isBackground &&
        ((PC.x-eye2.x)*(PC.x-eye2.x)+(PC.y-eye2.y)*(PC.y-eye2.y) > eyeD*eyeD);


    //smile
    float smileL = eye1.x;
    float smileR = eye2.x;
    float smileW = eyeD;
    isBackground = isBackground &&
        !(((PC.x > smileL) && (PC.x < smileR)) && (abs(PC.y+70.0-0.01*PC.x*PC.x) < smileW));

    // and where you draw the line? 
    vec3 colorPixel;
    if (isBackground) colorPixel = colorBackground;
    if (!isBackground) colorPixel = colorLine;
    fragColor = vec4(colorPixel, 1.0);
}
