void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    vec2 r=iResolution.xy;
	vec3 shapes;
    float dist;
    float time=iTime;

    vec2 uv = fragCoord/iResolution.xy;

    vec3 color = 0.5 + 0.5*cos(time+uv.xyx+vec3(.1,.2,.4));


	for(int i=0;i<3;i++) {
		vec2 uv;
        vec2 canvas=fragCoord.xy/iResolution.xy;
		canvas-=0.5;
		dist=length(canvas)+(sin(time)*.2)*(cos(time*.2)*.5);
		uv+=canvas+dist*(sin(time)+5.)*(sin(dist/dist*1.-time*.52))*2.*dist;
		shapes[i]+=5.9+length(abs(mod(uv,0.116)-.15))*112.;
	}

	fragColor=vec4((shapes*dist*color),1.);
}
