void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float centerx = mod(iTime*0.8,2.2);
    float wtfx = mod(-iTime*0.8+1.0,2.2);

    vec2 dist2center = vec2(abs(uv.x-centerx)*1.5, abs(uv.y-0.5));

    float radius = 0.2;

    float theBall = floor(sqrt(dist2center.x*dist2center.x + dist2center.y*dist2center.y)
                            + 1.0 - radius);

    float theMouth = 
         floor(uv.x+wtfx - abs(uv.y-0.5))
               //*(cos(iTime*4.0)+1.0) *10.0);
        *floor(cos(iTime*16.0)+1.0);

    vec3 col = vec3 (
        theBall+abs(theMouth),
        theBall+abs(theMouth),
        theBall+abs(theMouth)
    );

    col=abs(col);
     vec3 col2 = (
        vec3(
         	0.5*(0.5-abs(uv.y-0.5/3.0))*8.0,
            0.5*(0.5-abs(uv.x-0.5/3.0))*8.0,
            0.5*(0.5-abs(uv.x-0.5/3.0))*8.0
        )

    );

    vec3 col3 = (
        vec3(
            (0.5-abs(uv.x-0.5))+(0.5-abs(uv.y-0.5))*(cos(iTime*10.0)+1.0),
            (0.5-abs(uv.x-0.5))+(0.5-abs(uv.y-0.5))*(cos(iTime*20.0)+1.0),
            (0.5-abs(uv.x-0.5))+(0.5-abs(uv.y-0.5))*(cos(iTime*30.0)+1.0)
        )
    );

    vec3 amarillo = vec3 (1,1,0);
    vec3 blanco = vec3 (1,1,1);
    vec3 final = col+amarillo;
    final = vec3(clamp(final.x,0.0,1.0),clamp(final.y,0.0,1.0),clamp(final.z,0.0,1.0));
    final = final -vec3(floor(final.z),floor(final.z),floor(final.z));



    vec2 dist2point2 = vec2(abs(uv.x-0.75)*1.5, abs(uv.y-0.5));
    float pellet1 = 1.0-clamp(floor(sqrt(dist2point2.x*dist2point2.x + dist2point2.y*dist2point2.y)
                            + 1.0 - radius*0.3),0.0,1.0);

    dist2point2 = vec2(abs(uv.x-0.85)*1.5, abs(uv.y-0.5));
    pellet1 += 1.0-clamp(floor(sqrt(dist2point2.x*dist2point2.x + dist2point2.y*dist2point2.y)
                            + 1.0 - radius*0.3),0.0,1.0);


    dist2point2 = vec2(abs(uv.x-0.65)*1.5, abs(uv.y-0.5));
    pellet1 += 1.0-clamp(floor(sqrt(dist2point2.x*dist2point2.x + dist2point2.y*dist2point2.y)
                            + 1.0 - radius*0.3),0.0,1.0);

    vec3 colPellets = vec3 (
        pellet1,
        pellet1,
        pellet1
    );

    // Output to screen
    fragColor = vec4(final+colPellets,1.0);
}
