#define PI 3.141592653589793

const float strength = 0.3;

// https://www.shadertoy.com/view/4djSRW
float hash12(vec2 p) {
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 texCoord = fragCoord.xy / iResolution.xy;

	float progress = (sin(iTime * 0.5) + 1.) * 0.5;

    vec2 center = vec2(.5, .5);

    float strength = strength * sin(iTime);

    vec3 color = vec3(0.0);
    float total = 0.0;
    vec2 toCenter = center - texCoord;

    float offset = hash12(texCoord);

    for (float t = 0.0; t <= 20.0; t++) {
        float percent = (t + offset) / 20.0;
        float weight = 1.0 * (percent - percent * percent);
        color += texture(iChannel0, texCoord + toCenter * percent * strength).rgb * weight;
        total += weight;
    }

    fragColor = vec4(color / total, 1.0);
}
