// based on https://www.shadertoy.com/view/ltjXWW

#define B(u,d) texture(iChannel0, u/1024./L*(1.-S) + d )
//#define B(u,d) (2.*texture(iChannel0, u/1024./L)-1. + d )

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;
    float T = iTime, t=0.,  L = 20., S = iMouse.y/R.y;
    U -= .5*R;

    O -=O;

	for (float i=0.; i<L; i++)                  // loop on harmonics
	{   float k = mod(i-T,L),
		      a = 1.-cos(6.28*k/L),             // enveloppe
		      s = exp2(k);                      // fractal noise spectrum
		O += a/s * B(U*s, i/L); 
		t += a/s; 
	}
    O /= t;
    //O = .5+.5*O;

    //O*=O*O*3.;
}
