// From https://github.com/glslify/glsl-easings/blob/master/quintic-out.glsl
float qinticOut(float t) {
  return 1.0 - (pow(abs(t - 1.0), 5.0));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    uv *= 3.0;

    float l = length(uv);
    float t = qinticOut(fract(iTime*0.5));

    float breath = sin(t*6.28 - l*3.0);
    breath *= 1.0 - clamp(l, 0.0,1.0);

    fragColor = vec4(breath);
}
