const int index = 200;
vec2 c = vec2( -0.7093729, -0.28219825);
float er2 = 4.0; // er= er*er escape radius
float fColor =-0.7093729;
vec2 complex_square( vec2 v ) {
    return vec2(
        v.x * v.x - v.y * v.y,
        v.x * v.y * 2.0
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // compute coordinate 
   vec2 uv = (2.*fragCoord-iResolution.xy)/min(iResolution.x,iResolution.y);

   int count = 0;
   float juliaValue;
    // iterations 
   for ( int i = 0 ; i < index; i++ ) {

        uv = c + complex_square( uv );
        count = i;
        juliaValue =sqrt( uv.x*uv.x+uv.y*uv.y);

       if ( dot(uv,uv) > er2 ) {  break; }
   }

    // color 
   if (count == index-1) {
        fragColor = vec4(juliaValue,sin(iTime)/3.,sin(iTime)-fColor,1.0);
   } 
}
