void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int trunk = int(iTime);
    float remain = iTime - float(trunk);

    vec2 coord = fragCoord.xy/iResolution.xy;
    coord.y = abs(1.0 - coord.y);
    coord.x = coord.x + texture(iChannel1, coord ).x * remain * 2.0;
    vec4 color = texture(iChannel0, coord);
    //Remvoe Wrap - has to be an easier way to reconfigure sampler
    fragColor = coord.x >= 1.0 ? vec4(0.0) : color ;
    //Wrap
    //fragColor = texture(iChannel0, coord);
}
